import{j as s,$ as e,a0 as a,r as t,a1 as r}from"./lenis-react-BXHVlrKK.js";import{P as c}from"./PagesHeader-B8RZcGrB.js";import{B as l}from"./Title-Cql4Nz1w.js";function o(){return s.jsxs("div",{className:"app-container",children:[s.jsx(c,{title:"Выставка миниатюры «Восточные мотивы»",subtitle:"09.02.2024",backgroundImage:l}),s.jsx("section",{className:"PageAbout",children:s.jsx("div",{className:"container",children:s.jsx("div",{className:"row",children:s.jsxs("div",{className:"firstColumn col-xl-10 offset-xl-1 col-lg-12 col-md-12 col-sm-12 col-12",children:[s.jsx("p",{className:"text",children:"В залах Галереи изобразительного искусства Узбекистана организована выставка произведений миниатюры, посвящённая 583-летию со дня рождения великого поэта и мыслителя Алишера Навои под названием «Восточные мотивы». Идея проведения выставки принадлежит директору Галереи изобразительного искусства Узбекистана, доктору искусствознания Камоле Акиловой. Выставка организована Б. Хаджиметовым – председателем отделения миниатюры Творческого объединения художников Академии художеств Узбекистана."}),s.jsx("p",{className:"text",children:"Представленные работы выполнены в различных направлениях и стилях миниатюры, как в традициях классической миниатюры XV-XVI веков, так и в нетрадиционных, современных поисках. В работах художников мы можем увидеть композиции на историческую, лирическую и даже современную тематику. Материалы их творчества различны, и помимо традиционных – бумаги, картона, кожи, находят отражение в композициях больших и малых размеров и форм, деревянных лакировых панно, блюдах и шкатулках."}),s.jsx("p",{className:"text",children:"На этой выставке представлены работы мастеров миниатюрного искусства Узбекистана, которые стремились не только сохранить особенности традиционной миниатюрной живописи, но и создали свой творческий стиль и путь, такие как Н.Холматов, Ш.Шоахмедов, У.Касимов, К.Амонов, М.Сабиров, Б.Ходжиметов, К.Мирзаев, К.Абдуллаев, К.Шоисломов, З. Хакимов, Б. Хаджиметов, Р. Тохтаев, Г. Атабаев, Д. Полатова, а также включены работы молодых художников-миниатюристов, как Р. Алибоев, Б. Дедашев, Ш. Арифбоев, С. Джалиловой, Д. Хошимовой, Н. Ахмадалиевой."}),s.jsx("div",{className:"eastImg"}),s.jsx("p",{className:"text",children:"Выставка «Восточные мотивы» знакомит зрителя, как с образцами классической миниатюры, так и с результатом творческих изысканий. Кроме того, выставка обогащена видеопроектами с использованием возможностей компьютерных технологий. Это анимированные миниатюрные произведения аниматоров Н.Джураева и Дж.Каюмова, а также видеоуроки по работе с миниатюрными произведениями Б.Хаджиметова и его ученицы Н.Ахмадалиевой."})]})})})}),s.jsx(e,{}),s.jsx("div",{id:"PmS2WwDTR"})]})}a(document.getElementById("root")).render(s.jsx(t.StrictMode,{children:s.jsx(r,{root:!0,children:s.jsx(o,{})})}));
